import './ContactMe.css'
import pikachu4 from '../../img/pikachu4.png'
import {themeContext} from '../../Context'
import {useContext} from 'react'
// import {motion} from 'framer-motion'

export default function ContactMe() {

      // const transition = {duration :3 ,type:'spring' }


  const theme = useContext(themeContext)

  const darkMode = theme.state.darkMode;
  return (
    <div className='c-wrapper'>
      <div className="c-left">
        <div className="c-name">
          <span  style={{color:darkMode?'white':''}} >Get in Touch</span>
          <span>Contact Me</span>
        </div>
        <img
        // initial={{left:'25%'}}
        // whileInView={{left:'14%'}}
        // transition={transition}
        style={{width:'30%',height:250,marginLeft:'35%',}} src={pikachu4} alt="" />
        
        <div style={{top:'-10%',left:'100%'}} className="blur"></div>
            <div  style={{top:'40%',left:'10%',background:'#b8d0eb'}} className="blur"></div>
            <div style={{top:'-20%',left:'10%'}} className="blur"></div>

      </div>
      <div className="c-right">
        <div className="form">

          <input type="text" placeholder='Name' />
          <input type="email" placeholder='E-mail' />
          <textarea cols="10" placeholder='Message' ></textarea>
          <button style={{height:'50px',fontSize:'25px',fontWeight:'bold'}} className="button">Send Message</button>
        </div>
      </div>

    </div>
  )
}
