import './Intro.css'
import imgDesign from '../../img/abstract2.png'
import imgPic from '../../img/ProfilePIcY.png'
import crown from '../../img/crown.png'
import thumbUp from '../../img/thumbup.png'
import glasses from '../../img/Pikachu.png'
import FloatingDiv from '../FloatingDiv/FloatingDiv'
import {themeContext} from '../../Context'
import {useContext} from 'react'
// import {motion} from 'framer-motion'
import { animated, useSpring } from '@react-spring/web'


export default function Intro() {

    const [reloadKey, setReloadKey] = useState(0);

    const props = useSpring({
      from: { left: '-100%' },
      to: { left: '100%' },
      config: { duration: 3000 },
      reset: true,
      onRest: () => setReloadKey(reloadKey + 1)
    });
      
    const transition = {duration :3 ,type:'spring' }

    const theme = useContext(themeContext)

  const darkMode = theme.state.darkMode;
    return (
        <div className='intro'>
            <div className="i-left">
                <div className="i-name">
                    <span  style={{color: darkMode ? 'white' : '' }}>Hi these is</span>
                    <span>Mohammed Yunus</span>
                    <p>A Fullstack Developer to do the needful with affirmation and commitment and enhancing skills with fine work  </p>
                </div>
                <button className="button i-button">
                    Hire Me
                </button>
                <div className="i-icons">
                <i class="fa-brands fa-github"></i>
                <i class="fa-brands fa-linkedin"></i>
                <i class="fa-brands fa-square-instagram"></i>
                </div>
            </div>
            <div className="i-right">
                <img style={{width:'100%',height:600}} src={imgDesign} alt="" />
                <img src={imgPic} alt="" />
                <img
                // initial={{left:'-36%'}}
                // whileInView={{left:'-24%'}}
                // transition={transition}
                style={{width:'70%',height:400,top:'20%',left:'-80%'}} src={glasses} alt="" />

                <animated.div
                 style={{top:'80%',left:'12%',...props}}>
                    <FloatingDiv image={crown} txt1='Web' txt2='Developer'/>
                </animated.div>
                <div style={{top:'40%',left:'75%'}}>
                    <FloatingDiv image={thumbUp} txt1='Android' txt2='Developer'/>
                </div>
                {/* blur divs */}
                <div className="blur"></div>
                <div style={{background:'#c1f5ff',top:'50%',left:'-25%'}} className="blur"></div>
               
            </div>
        </div>
    )
}
