import './Testimonial.css'
import profile1 from '../../img/profile1.jpg'
import profile2 from '../../img/profile2.jpg'
import profile3 from '../../img/profile3.jpg'
import profile4 from '../../img/profile4.jpg'
// import profile5 from '../../img/profile5.jpg'
import { Swiper, SwiperSlide } from 'swiper/react'
import { Pagination } from 'swiper'
import 'swiper/css'
import 'swiper/css/pagination'

export default function Testimonial() {

    const clients = [
        {
            img: profile1,
            review: ' Lorem ipsum dolor sit amet consectetur, adipisicing elit. Corporis vitae temporibus, corrupti aspernatur expedita dolorum inventore consequatur qui dolore dolores magnam aliquid modi ipsam beatae non deleniti dolor fugit minus.'
        },
        {
            img: profile2,
            review: ' Lorem ipsum dolor sit amet consectetur, adipisicing elit. Corporis vitae temporibus, corrupti aspernatur expedita dolorum inventore consequatur qui dolore dolores magnam aliquid modi ipsam beatae non deleniti dolor fugit minus.'
        },
        {
            img: profile3,
            review: ' Lorem ipsum dolor sit amet consectetur, adipisicing elit. Corporis vitae temporibus, corrupti aspernatur expedita dolorum inventore consequatur qui dolore dolores magnam aliquid modi ipsam beatae non deleniti dolor fugit minus.'
        },
        {
            img: profile4,
            review: ' Lorem ipsum dolor sit amet consectetur, adipisicing elit. Corporis vitae temporibus, corrupti aspernatur expedita dolorum inventore consequatur qui dolore dolores magnam aliquid modi ipsam beatae non deleniti dolor fugit minus.'
        },
    ]

    return (
        <div className='t-wrapper'>
            <div className="t-heading">
                <span>Clients Always get</span>
                <span> Exceptional Work </span>
                <span> from me</span>
            </div>
           
            <Swiper
            modules={[Pagination]}
            slidesPerView={1}
            pagination={{clickable:true}}
            >
                {clients.map((client, index) => {
                    return (
                        <SwiperSlide key={index}>
                            <div className="testimonial">
                                <img src={client.img} alt="" />
                                <span>{client.review}</span>
                            </div>
                        </SwiperSlide>
                    )
                })}
            </Swiper>
            <div style={{top:'-30%',left:'-20%',background:'#c1f5ff'}} className="blur"></div>
            <div style={{top:'-60%',left:'10%',}} className="blur"></div>
            <div style={{top:'-50%',left:'10%',}} className="blur"></div>
        </div>
    )
}
