import Card from '../Card/Card'
import { useState } from 'react'
import './Services.css'
import emoji1 from '../../img/heartemoji.png'
import emoji2 from '../../img/glasses.png'
import emoji3 from '../../img/humble.png'

import resume from '../../Docs/sample.pdf'
import pikachu2 from '../../img/pikachu2.png'
import {themeContext} from '../../Context'
import {useContext} from 'react'

import { useSpring, animated } from 'react-spring'
import { useInView } from 'react-intersection-observer'


export default function Intro() {

    const [ref, inView] = useInView({ threshold: 0.5 });

  const props = useSpring({
    to: {
      opacity: inView ? 1 : 0,
      transform: inView ? 'translateX(0)' : 'translateX(-100%)'
    },
    config: { duration: 1000 }
  })

  const props2 = useSpring({
    to: {
      opacity: inView ? 1 : 0,
      transform: inView ? 'translateX(0)' : 'translateX(100%)'
    },
    config: { duration: 1000 }
  })

  const props3 = useSpring({
    to: {
      opacity: inView ? 1 : 0,
      transform: inView ? 'translateY(0)' : 'translateY(100%)'
    },
    config: { duration: 1000 }
  })
  
    const theme = useContext(themeContext)

  const darkMode = theme.state.darkMode;
    return (
        <div className='services'>
            <div className="s-left">
                <div className="s-name">
                    <span style={{color:darkMode?'white' :''}}>My Awesome</span>
                    <span>Services</span>
                    <p>Lorem ipsum, dolor sit amet consectetur adipisicing elit. Architecto ratione quos,
                        <br />
                        doloribus numquam omnis tempore animi
                    </p>
                </div>
                <a href={resume} download>
                <button className="button s-button">
                    Download CV
                </button>
                </a>

                <animated.img ref={ref}  style={{width:'40%',height:250,marginLeft:'60%',...props2}} src={pikachu2} alt="" />
                

            </div>
            <div className="s-right">
                <animated.div  style={{left:'50rem',...props3}}>
                    <Card
                        image={emoji1}
                        heading={'Design'}
                        details={['Figma, ', 'Sketch, ', <br />, 'Photoshop, ', 'Adobe, ', <br />, 'Adobe XD']}

                    />
                </animated.div>


                <div style={{left:'25rem',top:'30%'}}>
                    <Card
                        image={emoji2}
                        heading={'Developer'}
                        details={['Java, ', 'HTML, ', <br />, 'CSS, ', 'React JS, ', <br />, 'React Native']}

                    />
                </div>

                <div style={{left:'50rem',top:'65%'}}>
                    <Card
                        image={emoji3}
                        heading={'App Developer'}
                        details={['React Native  &', 'Redux ']}

                    />
                </div>
            </div>
            
            <div style={{top:'70%',left:'50%'}} className="blur"></div>
            <div style={{top:'40%',left:'0%',background:'#c1f5ff'}} className="blur"></div>
            <div style={{top:'10%',left:'0%'}} className="blur"></div>
        </div>
    )
}
