import './Portfolio.css'
import {Swiper,SwiperSlide} from 'swiper/react'
import 'swiper/css'
import img1 from '../../img/Business.png'
import img2 from '../../img/ECommerce.png'
import img3 from '../../img/Blog.png'
import img4 from '../../img/responsiveFinal.png'
import img5 from '../../img/Business2.png'
import {themeContext} from '../../Context'
import {useContext} from 'react'
 
export default function Portfolio() {
  const theme = useContext(themeContext)

  const darkMode = theme.state.darkMode;
  return (
    <div className='portfolio' id='Portfolio'>
      <span  style={{color:darkMode?'white':''}} >Recent Projects</span>
      <span>Portfolio</span>
        <Swiper
        
        spaceBetween={-200}
        slidesPerView={2}
        grabCursor={true}
        className='portfolio-slider'
        >
          
            <SwiperSlide>
                <img src={img1} alt="" />
            </SwiperSlide>
            <SwiperSlide>
                <img src={img2} alt="" />
            </SwiperSlide>
            <SwiperSlide>
                <img src={img3} alt="" />
            </SwiperSlide>
            <SwiperSlide>
                <img style={{height:'67%'}} src={img4} alt="" />
            </SwiperSlide>
            
        </Swiper>

         {/* Media query for 450px */}
      <style jsx>{`
        @media screen and (max-width: 450px) {
          .portfolio-slider {
            width: 100%;
            
          }
          .swiper-slide {
            width: 100% !important;
            display:flex;
            flex-direction:column;
          }
          .swiper-container {
            width: 100% !important;
            display:flex;
            flex-direction:column;
          }
          .swiper-pagination-bullet {
            display: none;
          }
          .swiper-button-next,
          .swiper-button-prev {
            // display: none !important;
          }
          .swiper-wrapper {
            // transform: none !important;
            // transition: none !important;
            display:flex;
            flex-direction:column;
          }
          .swiper-slide {
            margin: 0 !important;
            padding: 0 !important;
          }
          .swiper-slide img {
            width: 100% !important;
            height: auto !important;
          }
        }
      `}</style>
    </div>
  )
}
